<?php	if(!class_exists('Attribute')) require 'attribute.model.php';	$type = $_POST['type'];		switch($type){		case 'save':			if(Attribute::save($_POST)){				$result = array(					'status'		=> 1,					'msg'			=> '保存属性成功！',					'parentList'	=> Attribute::getParentList(),					'orderList'		=> Attribute::getParentList(0,false),				);				echo json_encode($result);			}else {				$result = array(					'status' 		=> 0,					'msg'			=> '保存属性失败！',					'parentList'	=> Attribute::getParentList(),					'orderList'		=> Attribute::getParentList(0,false),				);				echo json_encode($result);			}			break;					case 'changeOrder':			$pid = (int)$_POST['pid'];			echo json_encode(Attribute::getParentList($pid,false));			break;		default:			echo '非法类型！';			break;	}	exit;//End_php