<?php	if(!class_exists('Category')) require 'category.model.php';	$type = $_POST['type'];		switch($type){		case 'save':			if(Category::saveCategory($_POST)){				$result = array(					'status'		=> 1,					'msg'			=> '保存分类成功！',					'parentList'	=> Category::getParentList(),					'orderList'		=> Category::getParentList(0,false),				);				echo json_encode($result);			}else {				$result = array(					'status' 		=> 0,					'msg'			=> '保存分类失败！',					'parentList'	=> Category::getParentList(),					'orderList'		=> Category::getParentList(0,false),				);				echo json_encode($result);			}			break;					case 'changeOrder':			$pid = (int)$_POST['pid'];			echo json_encode(Category::getParentList($pid,false));			break;		default:			echo '非法类型！';			break;	}	exit;//End_php