<?php	if(!class_exists('Comment')) require 'comment.model.php';	$type = $_POST['type'];		switch($type){		case 'save':			if(Comment::save($_POST)){				$result = array(					'status'		=> 1,					'msg'			=> '保存评论成功！',					'comment'		=> Comment::loadComments($_POST['productId'],mysql_insert_id($db)),				);				echo json_encode($result);			}else {				$result = array(					'status' 		=> 0,					'msg'			=> '保存评论失败！'.mysql_error($db),				);				echo json_encode($result);			}			break;					case 'loadComments':			$productId = (int)$_POST['productId'];			echo json_encode(Comment::loadComments($productId));			break;		default:			echo '非法类型！';			break;	}	exit;//End_php